<?php $LangId=  AuxTools::GetCurrentLanguageIDJoomla();$sess=JFactory::getApplication()->getSession();$productsid = $sess->get('products', array());$ptotal=0;$total=0;$curr=bll_currencies::getActiveCurrency();?><div class="cart-holder">    <?php foreach($productsid as $id => $cant):        $product = new bll_product($id);        $total+=$product->SalePrice*$cant;        $ptotal+=$cant;        ?>    <?php endforeach; ?>        <a href="/index.php?option=com_catalog&view=sales">            <i class="fa fa-shopping-cart fa-2x"></i>: <?php echo AuxTools::MoneyFormat($total, $curr->CurrCode, $curr->Rate); ?>         </a>    </div>