<?php $LangId=  AuxTools::GetCurrentLanguageIDJoomla();$sess=JFactory::getApplication()->getSession();$productsid = $sess->get('products', array());$ptotal=0;$total=0;?><div class="cart-holder">    <?php foreach($productsid as $id => $cant):        $product = new bll_product($id);        $total+=$product->SalePrice*$cant;        $ptotal+=$cant;        ?>    <?php endforeach; ?>    <p id="cart_detail_str"><?php echo $ptotal;  ?> <?php echo JText::_('COM_CATALOG_CART_ARTICLES_FOR')." ".AuxTools::MoneyFormat($total); ?> </p>            <a href="/index.php?option=com_catalog&view=sales">            <i class="fa fa-shopping-cart fa-2x"></i>            <?php echo JText::_('COM_CATALOG_CHECKOUT'); ?>        </a>    </div>