<?php $LangId=  AuxTools::GetCurrentLanguageIDJoomla();$sess=JFactory::getApplication()->getSession();$selcur = bll_currencies::getActiveCurrency();$curr= new bll_currencies(-1);$currs= array();foreach($curr->findAll() as $cur){   if($cur->Id == $selcur->Id)   {       $currs["#__".$cur->Id]=$cur->CurrCode;   }   else   {       $currs[$cur->Id]=$cur->CurrCode;   }}?><div class="curr-holder">    <?php        $form = Form::getInstance();        $form->setLayout(FormLayouts::FORMS_UL_LAYOUT);        $form->SelectBox('curr', $currs, '', '', false, array('onChange'=>'document.curr_sel.submit()'));        echo $form->Render('/index.php?option=com_catalog&task=setcurr', array(), false,'curr_sel');        ?></div>